mapper.select:
SELECT id, user_id, post_id, browse_time, created_at, updated_at
FROM browse_history
WHERE user_id = #{userId}
ORDER BY browse_time DESC
LIMIT #{offset}, #{limit}

SELECT id, user_id, post_id, browse_time, created_at, updated_at
FROM browse_history
WHERE user_id = #{userId}
AND browse_time >= #{startTime}
AND browse_time <= #{endTime}
ORDER BY browse_time DESC

SELECT COUNT(*) > 0
FROM browse_history
WHERE user_id = #{userId} AND post_id = #{postId}

SELECT COUNT(*)
FROM browse_history
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM browse_history
WHERE user_id = #{userId}
AND browse_time < #{expireTime}
