mapper.select:
SELECT p.id, p.user_id, p.content, p.like_count, p.comment_count, p.is_deleted, p.created_at, p.updated_at FROM post p WHERE p.id IN
#{id}
AND p.is_deleted = 0 ORDER BY p.updated_at DESC, p.id DESC

mapper.update:
UPDATE post
SET content = #{content}, like_count = #{likeCount}, comment_count = #{commentCount}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT id, user_id AS userId FROM post WHERE id IN
#{id}
AND is_deleted = 0
