mapper.select:
SELECT id, resource_type, resource_id, date, visitor_id, created_at, updated_at
FROM uv_statistics
WHERE resource_type = #{resourceType}
AND resource_id = #{resourceId}
AND date = #{date}
ORDER BY created_at DESC

SELECT id, resource_type, resource_id, date, visitor_id, created_at, updated_at
FROM uv_statistics
WHERE resource_type = #{resourceType}
AND resource_id = #{resourceId}
AND date >= #{startDate}
AND date <= #{endDate}
ORDER BY date DESC, created_at DESC

SELECT COUNT(DISTINCT visitor_id)
FROM uv_statistics
WHERE resource_type = #{resourceType}
AND resource_id = #{resourceId}
AND date = #{date}

SELECT COUNT(DISTINCT visitor_id)
FROM uv_statistics
WHERE resource_type = #{resourceType}
AND resource_id = #{resourceId}
AND date >= #{startDate}
AND date <= #{endDate}
