mapper.update:
UPDATE user_base
SET last_login_time = #{lastLoginTime}, update_time = NOW()
WHERE id = #{userId}

UPDATE user_base
username = #{username}, email = #{email}, password_hash = #{passwordHash}, salt = #{salt}, phone = #{phone}, user_type = #{userType}, status = #{status}, password_update_time = #{passwordUpdateTime}, update_time = NOW()
WHERE id = #{id}

mapper.insert:
INSERT INTO user_base (
    username, email, password_hash, salt, phone, status, user_type,
    create_time, update_time, password_update_time
) VALUES (
    #{username}, #{email}, #{passwordHash}, #{salt}, #{phone}, #{status}, #{userType},
    #{createTime}, #{updateTime}, #{passwordUpdateTime}
)

mapper.update:
UPDATE user_base
SET status = #{status}, update_time = NOW()
WHERE id = #{userId}

mapper.select:
SELECT id, username, email, password_hash, salt, phone, status, user_type, create_time, update_time, last_login_time, password_update_time FROM user_base WHERE id IN
#{id}
AND status != 0 ORDER BY
#{id}
